import { useContext, useState } from "react";
import { Button, Container, Form } from "react-bootstrap";
import { Helmet } from "react-helmet-async";
import { Link, useLocation } from "react-router-dom";
import { Store } from "../Store";

export default function SigninScreen() {
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const { state, dispatch: ctxDispatch } = useContext(Store);

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      console.log({ email, password });
      const result = await fetch('/api/users/signin', {
        method: 'POST',
        headers: new Headers({ "content-type": "application/json" }),
        body: JSON.stringify({ email, password })
      });

      if (!result.ok)
        throw new Error((await result.json()).message);
      const data = await result.json();

      ctxDispatch({ type: 'USER_SIGNIN', payload: data });
    } catch (err) { }
  }
  return (
    <Container className="small-container">
      <Helmet>
        <title>Sign In</title>
      </Helmet>
      <h1 className="my-3">Sign In</h1>
      <Form>
        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control type="email" required onChange={(e) => setEmail(e.target.value)} />
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" required onChange={(e) => setPassword(e.target.value)} />
        </Form.Group>
        <div className="mb-3">
          <Button onClick={submitHandler} type="submit">Sign In</Button>
        </div>
        <div className="mb-3">
          Ya new 'round ere?{' '}
          <Link className="text-decoration-none link-primary" to={`/signup?redirect=${redirect}`} type="submit">Create an account</Link>
        </div>
      </Form>
    </Container>
  );
}
