import { useEffect, useReducer } from 'react';
import { Badge, Button, Card, Col, ListGroup, Row } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import Rating from '../components/Rating';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return { ...state, product: action.payload, loading: false };
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
}


function ProductScreen() {
  const params = useParams();
  const { slug } = params;
  const [{ loading, error, product }, dispatch] = useReducer(reducer, {
    product: [],
    loading: true,
    error: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await fetch(`/api/products/${slug}`);
        const item = await result.json();
        dispatch({ type: 'FETCH_SUCCESS', payload: item });
        console.log('Success ma boy fr fr');
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: err.message });
        console.log('Yikes ma boy fr fr');
      }

    };
    fetchData();
  }, [slug]);


  return (
    loading ? (<div>Loading...</div>)
      : error ? (
        <div>{error}</div>
      ) : (
        <div>
          <Row className=''>
            <h1 className='display-4 fw-bold mx-auto'>{product.name}</h1>
          </Row>
          <Row>
            <Col md={6}>
              <img className='img-fluid' src={product.image} alt={product.name}>
              </img>
            </Col>
            <Col md={6}>
              <ListGroup variant='flush'>
                <ListGroup.Item>
                  <Rating rating={product.rating} numReviews={product.numReviews}>
                  </Rating>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Row>
                    <Col>
                      <b>Price: </b>
                      ${product.price}
                    </Col>
                    <Col>

                    </Col>
                  </Row>
                </ListGroup.Item>
                <ListGroup.Item>
                  <b>Description:</b>
                  <p>{product.description}</p>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Row>
                    <Col>Status:</Col>
                    <Col>{product.countInStock > 0 ?
                      (<Badge bg="success">Available</Badge>)
                      :
                      (<Badge bg="danger">Unavailable</Badge>)
                    }</Col>
                  </Row>
                </ListGroup.Item>
                {product.countInStock > 0 && (
                  <ListGroup.Item>
                    <div className='d-grid'>
                      <Button className='border-0' variant='primary'>
                        Add 2 Cart
                      </Button>
                    </div>
                  </ListGroup.Item>
                )}
              </ListGroup>
            </Col>
          </Row>
        </div>
      )
  );
}

export default ProductScreen;
